ntById(skill).value);	if(skillValue <= 0){		alert("No points!");	} else {		// add skill point		newSkillValue = skillValue - 1;		document.getElementById(skill).value = '';		document.getElementById(skill).value = newSkillValue;		// next subtract skill point from characters total skill points		newCharSkills = charSkills + 1;		document.getElementById('char_skills').value = newCharSkills;	}}$(document).ready(function() {     $('input[type="text"]').addClass("idleField");    $('input[type="text"]').focus(function() {        $(this).removeClass("idleField").addClass("focusField");        if (this.value == this.defaultValue){            this.value = '';        }        if(this.value != this.defaultValue){            this.select();        }    });    $('input[type="text"]').blur(function() {        $(this).removeClass("focusField").addClass("idleField");    });    $('input[type="password"]').addClass("idleField");    $('input[type="password"]').focus(function() {        $(this).removeClass("idleField").addClass("focusField");        if (this.value == this.defaultValue){            this.value = '';        }        if(this.value != this.defaultValue){            this.select();        }    });    $('input[type="password"]').blur(function() {        $(this).removeClass("focusField").addClass("idleField");    });}); </script><!DOCTYPE html><html><head>	<meta charset="utf-8">	<title>Character Creation</title>	<link rel="stylesheet" href="interface/styles/layout.css">	<script src="interface/scripts/jquery.anythingslider.js"></script>	<link rel="stylesheet" href="interface/styles/theme-metallic.css">	<script>		$(function(){			$('#slider1').anythingSlider({				startStopped    : true,  // If autoPlay is on, this can force it to start stopped				width           : 800,   // Override the default CSS width				theme           : 'metallic',				easing          : 'easeInOutBack',				toggleControls  : false,				enableNavigation: false,