l5">		<div class="textSlide">			<h2>Character Basics</h2>			<br>			<div class="contentRight"><img src="interface/img/char_creator.jpg"></div>			<? echo $character->characterBuilder("character_basics"); ?>		</div>	</li>	<li class="panel5">		<div class="textSlide">			<h2>Character Look</h2>			<br>			<? echo $character->characterBuilder("character_look"); ?>		</div>	</li>	<li class="panel5">		<div class="textSlide">			<h2>Character Review</h2>			<br>			<? echo $character->characterBuilder("review_character"); ?>		</div>	</li></ul></form><? } else {?><h1>Character Creation Complete!</h1><? } ?>